package demo;

public class StringQ1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1 = "microsoft";
		System.out.println(s1.contains("s"));
		System.out.println(s1.contains("S"));
		s1=s1.toUpperCase();
		System.out.println(s1.contains("S"));
		
		char ch[]=s1.toCharArray();
		System.out.println(ch);
		
	}

	
		
		 
		 
}
